-- ----------------------------
-- Procedure structure for `ebshop_GetProductShowData`
-- ----------------------------
DROP PROCEDURE IF EXISTS `ebshop_GetProductShowData`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `ebshop_GetProductShowData`(p_id int)
BEGIN
   
  set @brandid=(select annex11 from eb_newscontent WHERE id=p_id);

  
  set @siteid=(select siteid from eb_newscontent WHERE id=p_id);

   
    set @annex16=(select annex16 from eb_newscontent WHERE id=p_id);

  
	SELECT * from ebshop_productsimg where ProductID=p_id;

   
   select a.id,a.BuyProductId,a.GiftProductId,a.Quantity,a.EndDateTime,b.SmallPic,b.NewsTitle  FROM ebshop_gift a,eb_newscontent b where  a.EndDateTime >NOW() and   a.GiftProductId = b.ID and a.BuyProductId =p_id;

   
    SELECT * from ebshop_p_userbook where ProductID=p_id;

  
    SELECT a.*,b.annex16 as Price,b.annex2 as ShiChangJia from ebshop_P_BestGroup a  LEFT OUTER JOIN eb_newscontent b on a.GoodsID=b.ID where a.typeid=2 and a.productid=p_id;

  
     SELECT a.*,b.annex16 as Price,b.annex2 as ShiChangJia from ebshop_P_BestGroup a  LEFT OUTER JOIN eb_newscontent b on a.GoodsID=b.ID where a.typeid=1 and a.productid=p_id;
 
  
   SELECT * from eb_newscontent where id!=p_id and annex11=@brandid  and SiteID=@siteid ORDER BY id desc LIMIT 6;

   
   
   SELECT * from eb_newscontent where Annex25=1 and id!=p_id and annex16< @annex16 + 50  and @annex16>@annex16 - 50 and SiteID=@siteid ORDER BY id desc LIMIT 6;

   
   SELECT * from ebshop_typerelationproduct where item>0 and ProductID=p_id GROUP BY attributeId;

    
END
;;
DELIMITER ;

