-- ----------------------------
-- Procedure structure for `ebshop_OrderCount`
-- ----------------------------
DROP PROCEDURE IF EXISTS `ebshop_OrderCount`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `ebshop_OrderCount`(p_datetype varchar(50),p_yearormoth int,p_field varchar(50),out p_sumcount int,out p_maxcount int)
BEGIN
	if(p_datetype='m') THEN
			if(p_field='l') THEN
				set p_sumcount=(select count(id) as 'tc' from ebshop_buy_orders where DATE_FORMAT(OrderAddDate,'%Y')=p_yearormoth);
			  set p_maxcount=(select count(id) as 'mc' from ebshop_buy_orders where DATE_FORMAT(OrderAddDate,'%Y')=p_yearormoth GROUP BY DATE_FORMAT(OrderAddDate,'%Y%m') order by count(id) desc limit 1);
				select DATE_FORMAT(OrderAddDate,'%m') as 'm',count(id) as 'c',convert(round((count(id)/p_sumcount),2)*100,signed) as 's' from ebshop_buy_orders where DATE_FORMAT(OrderAddDate,'%Y')=p_yearormoth GROUP BY DATE_FORMAT(OrderAddDate,'%m');
		  elseif(p_field='e') THEN 
				set p_sumcount=(select sum(OrderTotal) as 'tc' from ebshop_buy_orders where DATE_FORMAT(OrderAddDate,'%Y')=p_yearormoth);
			  set p_maxcount=(select sum(OrderTotal) as 'mc' from ebshop_buy_orders where DATE_FORMAT(OrderAddDate,'%Y')=p_yearormoth GROUP BY DATE_FORMAT(OrderAddDate,'%Y%m') order by count(id) desc limit 1);
				select DATE_FORMAT(OrderAddDate,'%m') as 'm',sum(OrderTotal) as 'c',convert(round((sum(OrderTotal)/p_sumcount),2)*100,signed) as 's' from ebshop_buy_orders where DATE_FORMAT(OrderAddDate,'%Y')=p_yearormoth GROUP BY DATE_FORMAT(OrderAddDate,'%m');
			elseif(p_field='r') THEN
				set p_sumcount=(select sum(OrderProfit) as 'tc' from ebshop_buy_orders where DATE_FORMAT(OrderAddDate,'%Y')=p_yearormoth);
			  set p_maxcount=(select sum(OrderProfit) as 'mc' from ebshop_buy_orders where DATE_FORMAT(OrderAddDate,'%Y')=p_yearormoth GROUP BY DATE_FORMAT(OrderAddDate,'%Y%m') order by count(id) desc limit 1);
				select DATE_FORMAT(OrderAddDate,'%m') as 'm',sum(OrderProfit) as 'c',convert(round((sum(OrderProfit)/p_sumcount),2)*100,signed) as 's' from ebshop_buy_orders where DATE_FORMAT(OrderAddDate,'%Y')=p_yearormoth GROUP BY DATE_FORMAT(OrderAddDate,'%m');
			end if;
	else
			if(p_field='l') THEN
				set p_sumcount=(select count(id) as 'tc' from ebshop_buy_orders where DATE_FORMAT(OrderAddDate,'%Y%m')=p_yearormoth);
			  set p_maxcount=(select count(id) as 'mc' from ebshop_buy_orders where DATE_FORMAT(OrderAddDate,'%Y%m')=p_yearormoth GROUP BY DATE_FORMAT(OrderAddDate,'%d') order by count(id) desc limit 1);
				select DATE_FORMAT(OrderAddDate,'%d') as 'm',count(id) as 'c',convert(round((count(id)/p_sumcount),2)*100,signed) as 's' from ebshop_buy_orders where DATE_FORMAT(OrderAddDate,'%Y%m')=p_yearormoth GROUP BY DATE_FORMAT(OrderAddDate,'%d');
		  elseif(p_field='e') THEN
				set p_sumcount=(select sum(OrderTotal) as 'tc' from ebshop_buy_orders where DATE_FORMAT(OrderAddDate,'%Y%m')=p_yearormoth);
