				select DATE_FORMAT(OrderAddDate,'%d') as 'm',sum(OrderTotal) as 'c',convert(round((sum(OrderTotal)/p_sumcount),2)*100,signed) as 's' from ebshop_buy_orders where DATE_FORMAT(OrderAddDate,'%Y%m')=p_yearormoth GROUP BY DATE_FORMAT(OrderAddDate,'%d');
			elseif(p_field='r') THEN
				set p_sumcount=(select sum(OrderProfit) as 'tc' from ebshop_buy_orders where DATE_FORMAT(OrderAddDate,'%Y%m')=p_yearormoth);
			  set p_maxcount=(select sum(OrderProfit) as 'mc' from ebshop_buy_orders where DATE_FORMAT(OrderAddDate,'%Y%m')=p_yearormoth GROUP BY DATE_FORMAT(OrderAddDate,'%d') order by count(id) desc limit 1);
				select DATE_FORMAT(OrderAddDate,'%d') as 'm',sum(OrderProfit) as 'c',convert(round((sum(OrderProfit)/p_sumcount),2)*100,signed) as 's' from ebshop_buy_orders where DATE_FORMAT(OrderAddDate,'%Y%m')=p_yearormoth GROUP BY DATE_FORMAT(OrderAddDate,'%d');
			end if;
  end if;
END
